{code:json}{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Cleansheet Learner Opportunity Profile Schema",
  "type": "object",
  "required": ["opportunityId", "learnerId", "title", "organization", "location", "status", "createdDate", "version"],
  "properties": {
    "opportunityId": {
      "type": "string",
      "description": "Unique identifier for this opportunity"
    },
    "learnerId": {
      "type": "string",
      "description": "Reference to the learner who owns this opportunity"
    },
    "title": {
      "type": "string",
      "description": "Job title or position name"
    },
    "organization": {
      "type": "object",
      "required": ["name"],
      "properties": {
        "name": {
          "type": "string",
          "description": "Organization or company name"
        },
        "size": {
          "type": "string",
          "enum": ["Startup (1-50)", "Small (51-200)", "Medium (201-1000)", "Large (1001-5000)", "Enterprise (5000+)", "Unknown"]
        },
        "industry": {
          "type": "string",
          "description": "Industry sector or domain"
        },
        "website": {
          "type": "string",
          "format": "uri",
          "description": "Organization website URL"
        },
        "description": {
          "type": "string",
          "description": "Brief description of the organization"
        },
        "ratings": {
          "type": "object",
          "properties": {
            "glassdoor": {
              "type": "number",
              "minimum": 1,
              "maximum": 5
            },
            "indeed": {
              "type": "number",
              "minimum": 1,
              "maximum": 5
            },
            "linkedIn": {
              "type": "number",
              "minimum": 1,
              "maximum": 5
            }
          }
        }
      }
    },
    "location": {
      "type": "object",
      "required": ["type"],
      "properties": {
        "type": {
          "type": "string",
          "enum": ["remote", "hybrid", "onsite"]
        },
        "city": {
          "type": "string",
          "description": "City name if onsite or hybrid"
        },
        "state": {
          "type": "string",
          "description": "State or province"
        },
        "country": {
          "type": "string",
          "description": "Country"
        },
        "timezone": {
          "type": "string",
          "description": "Required timezone for remote/hybrid work"
        },
        "travelRequired": {
          "type": "string",
          "enum": ["none", "minimal", "occasional", "frequent"],
          "description": "Level of travel required"
        }
      }
    },
    "compensation": {
      "type": "object",
      "properties": {
        "salary": {
          "type": "object",
          "properties": {
            "currency": {
              "type": "string",
              "default": "USD"
            },
            "min": {
              "type": "number",
              "description": "Minimum salary range"
            },
            "max": {
              "type": "number",
              "description": "Maximum salary range"
            },
            "frequency": {
              "type": "string",
              "enum": ["hourly", "annual"],
              "default": "annual"
            }
          }
        },
        "equity": {
          "type": "boolean",
          "description": "Equity compensation available"
        },
        "benefits": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "List of benefits offered"
        },
        "bonusStructure": {
          "type": "string",
          "description": "Description of bonus or commission structure"
        }
      }
    },
    "jobDetails": {
      "type": "object",
      "properties": {
        "description": {
          "type": "string",
          "description": "Full job description"
        },
        "requirements": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Required qualifications and skills"
        },
        "preferredQualifications": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Nice-to-have qualifications"
        },
        "responsibilities": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Key job responsibilities"
        },
        "technologies": {
          "type": "array",
          "items": {
            "type": "object",
            "required": ["name"],
            "properties": {
              "name": {
                "type": "string"
              },
              "required": {
                "type": "boolean",
                "default": false
              },
              "experienceLevel": {
                "type": "string",
                "enum": ["beginner", "intermediate", "advanced", "expert"]
              }
            }
          }
        },
        "experienceLevel": {
          "type": "string",
          "enum": ["entry", "mid", "senior", "lead", "executive"]
        },
        "employmentType": {
          "type": "string",
          "enum": ["full-time", "part-time", "contract", "contract-to-hire", "internship"]
        }
      }
    },
    "stakeholders": {
      "type": "array",
      "description": "People involved in the hiring process",
      "items": {
        "type": "object",
        "required": ["stakeholderId", "name", "role"],
        "properties": {
          "stakeholderId": {
            "type": "string",
            "description": "Unique identifier for this stakeholder"
          },
          "name": {
            "type": "string",
            "description": "Stakeholder's name"
          },
          "role": {
            "type": "string",
            "description": "Their role in the hiring process"
          },
          "title": {
            "type": "string",
            "description": "Their job title"
          },
          "email": {
            "type": "string",
            "format": "email"
          },
          "linkedIn": {
            "type": "string",
            "format": "uri"
          },
          "phone": {
            "type": "string"
          },
          "notes": {
            "type": "string",
            "description": "Notes about interactions with this person"
          },
          "relationship": {
            "type": "string",
            "enum": ["recruiter", "hiring-manager", "team-member", "hr", "executive", "referral", "other"]
          }
        }
      }
    },
    "applicationProcess": {
      "type": "object",
      "properties": {
        "source": {
          "type": "string",
          "enum": ["linkedin", "indeed", "glassdoor", "company-website", "referral", "recruiter", "job-board", "networking", "other"]
        },
        "applicationUrl": {
          "type": "string",
          "format": "uri"
        },
        "applicationDeadline": {
          "type": "string",
          "format": "date-time"
        },
        "applicationSubmitted": {
          "type": "boolean",
          "default": false
        },
        "applicationDate": {
          "type": "string",
          "format": "date-time"
        },
        "interviewProcess": {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "stage": {
                "type": "string",
                "enum": ["phone-screen", "technical-interview", "behavioral-interview", "panel-interview", "on-site", "final-interview", "reference-check", "other"]
              },
              "description": {
                "type": "string"
              },
              "duration": {
                "type": "string",
                "description": "Expected duration (e.g., '1 hour', '2-3 hours')"
              },
              "stakeholders": {
                "type": "array",
                "items": {
                  "type": "string"
                },
                "description": "References to stakeholder IDs who participate in this stage"
              }
            }
          }
        }
      }
    },
    "actions": {
      "type": "array",
      "description": "Action items and tasks related to this opportunity",
      "items": {
        "type": "object",
        "required": ["actionId", "action", "deadline", "status"],
        "properties": {
          "actionId": {
            "type": "string",
            "description": "Unique identifier for this action"
          },
          "action": {
            "type": "string",
            "description": "Description of the action to be taken"
          },
          "category": {
            "type": "string",
            "enum": ["research", "application", "networking", "preparation", "follow-up", "interview", "negotiation", "documentation", "other"]
          },
          "priority": {
            "type": "string",
            "enum": ["low", "medium", "high", "critical"],
            "default": "medium"
          },
          "stakeholders": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "References to stakeholder IDs who are involved in this action"
          },
          "deadline": {
            "type": "string",
            "format": "date-time",
            "description": "When this action must be completed"
          },
          "status": {
            "type": "string",
            "enum": ["pending", "in-progress", "completed", "cancelled", "overdue"],
            "default": "pending"
          },
          "completedDate": {
            "type": "string",
            "format": "date-time"
          },
          "notes": {
            "type": "string",
            "description": "Additional notes or context about this action"
          },
          "attachments": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "name": {
                  "type": "string"
                },
                "url": {
                  "type": "string",
                  "format": "uri"
                },
                "type": {
                  "type": "string",
                  "enum": ["resume", "cover-letter", "portfolio", "reference", "document", "other"]
                }
              }
            }
          },
          "estimatedDuration": {
            "type": "string",
            "description": "Estimated time to complete (e.g., '2 hours', '1 day')"
          }
        }
      }
    },
    "analysis": {
      "type": "object",
      "description": "Cleansheet platform analysis of this opportunity",
      "properties": {
        "matchScore": {
          "type": "number",
          "minimum": 0,
          "maximum": 100,
          "description": "AI-generated match score based on learner profile"
        },
        "skillGaps": {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "skill": {
                "type": "string"
              },
              "required": {
                "type": "boolean"
              },
              "currentLevel": {
                "type": "string",
                "enum": ["none", "novice", "beginner", "intermediate", "advanced", "expert"]
              },
              "requiredLevel": {
                "type": "string",
                "enum": ["novice", "beginner", "intermediate", "advanced", "expert"]
              },
              "developmentSuggestions": {
                "type": "array",
                "items": {
                  "type": "string"
                }
              }
            }
          }
        },
        "salaryComparison": {
          "type": "object",
          "properties": {
            "marketAverage": {
              "type": "number"
            },
            "percentile": {
              "type": "number",
              "minimum": 0,
              "maximum": 100
            },
            "comparison": {
              "type": "string",
              "enum": ["below-market", "market-rate", "above-market", "premium"]
            }
          }
        },
        "riskFactors": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Potential concerns or red flags identified"
        },
        "competitiveAdvantages": {
          "type": "array",
          "items": {
            "type": "string"
          },
          "description": "Learner's advantages for this role"
        }
      }
    },
    "status": {
      "type": "string",
      "enum": ["researching", "preparing", "applied", "interviewing", "negotiating", "offered", "accepted", "declined", "rejected", "withdrawn"],
      "description": "Current status of this opportunity"
    },
    "tags": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Custom tags for organization and filtering"
    },
    "notes": {
      "type": "string",
      "description": "General notes about this opportunity"
    },
    "createdDate": {
      "type": "string",
      "format": "date-time"
    },
    "lastModified": {
      "type": "string",
      "format": "date-time"
    },
    "version": {
      "type": "string",
      "description": "Schema version"
    }
  }
}{code}

{code:json}{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Cleansheet Learner Assessment Data Model",
  "type": "object",
  "required": ["userName", "assessmentId", "careerPaths", "skillAssessments", "competencyAssessments", "assessmentDate", "version"],
  "properties": {
    "userName": {
      "type": "string",
      "description": "Learner's full name"
    },
    "assessmentId": {
      "type": "string",
      "description": "Unique identifier for this assessment instance"
    },
    "careerPaths": {
      "type": "object",
      "description": "Career path interest and proficiency levels",
      "properties": {
        "curated": {
          "type": "array",
          "items": {
            "type": "object",
            "required": ["pathName", "interestLevel", "currentProficiency", "desiredProficiency"],
            "properties": {
              "pathName": {
                "type": "string",
                "enum": [
                  "Citizen Developer",
                  "Full Stack Developer",
                  "Cloud Operations",
                  "Cloud Computing",
                  "Network Operations",
                  "Security Operations",
                  "Data Analytics",
                  "AI/ML Engineer",
                  "Project Manager"
                ]
              },
              "interestLevel": {
                "type": "integer",
                "minimum": 1,
                "maximum": 5,
                "description": "Level of interest: 1=No Interest, 2=Low, 3=Moderate, 4=High, 5=Very High"
              },
              "currentProficiency": {
                "type": "string",
                "enum": ["novice", "beginner", "intermediate", "advanced", "expert"],
                "description": "Self-assessed current skill level"
              },
              "desiredProficiency": {
                "type": "string",
                "enum": ["novice", "beginner", "intermediate", "advanced", "expert"],
                "description": "Target skill level within 12 months"
              },
              "timeline": {
                "type": "string",
                "enum": ["3-months", "6-months", "12-months", "18-months", "24-months"],
                "description": "Expected timeline to reach desired proficiency"
              }
            }
          }
        },
        "adjacent": {
          "type": "array",
          "items": {
            "type": "object",
            "required": ["pathName", "interestLevel"],
            "properties": {
              "pathName": {
                "type": "string",
                "enum": [
                  "General Manager",
                  "Business Development Manager",
                  "Program Manager",
                  "Product Manager"
                ]
              },
              "interestLevel": {
                "type": "integer",
                "minimum": 1,
                "maximum": 5
              }
            }
          }
        }
      }
    },
    "skillAssessments": {
      "type": "object",
      "description": "Technical and soft skill assessments",
      "properties": {
        "technical": {
          "type": "array",
          "items": {
            "type": "object",
            "required": ["skillName", "category", "currentLevel", "desiredLevel", "priority"],
            "properties": {
              "skillName": {
                "type": "string",
                "description": "Name of the technical skill"
              },
              "category": {
                "type": "string",
                "enum": [
                  "Programming Languages",
                  "Frameworks & Libraries",
                  "Databases",
                  "Cloud Platforms",
                  "DevOps & CI/CD",
                  "Security Tools",
                  "Analytics Tools",
                  "Project Management Tools",
                  "Design Tools",
                  "Testing Frameworks",
                  "API Development",
                  "Mobile Development",
                  "Machine Learning",
                  "Infrastructure",
                  "Monitoring & Logging"
                ]
              },
              "currentLevel": {
                "type": "string",
                "enum": ["novice", "beginner", "intermediate", "advanced", "expert"]
              },
              "desiredLevel": {
                "type": "string",
                "enum": ["novice", "beginner", "intermediate", "advanced", "expert"]
              },
              "priority": {
                "type": "string",
                "enum": ["low", "medium", "high", "critical"]
              },
              "lastUsed": {
                "type": "string",
                "format": "date",
                "description": "Date when skill was last applied professionally"
              },
              "yearsExperience": {
                "type": "number",
                "minimum": 0,
                "description": "Total years of experience with this skill"
              },
              "confidenceLevel": {
                "type": "integer",
                "minimum": 1,
                "maximum": 10,
                "description": "Confidence in self-assessment (1-10 scale)"
              }
            }
          }
        },
        "soft": {
          "type": "array",
          "items": {
            "type": "object",
            "required": ["skillName", "currentLevel", "desiredLevel", "priority"],
            "properties": {
              "skillName": {
                "type": "string",
                "enum": [
                  "Leadership",
                  "Communication",
                  "Problem Solving",
                  "Critical Thinking",
                  "Team Collaboration",
                  "Time Management",
                  "Adaptability",
                  "Mentoring",
                  "Presentation Skills",
                  "Conflict Resolution",
                  "Strategic Thinking",
                  "Emotional Intelligence",
                  "Customer Focus",
                  "Innovation",
                  "Decision Making"
                ]
              },
              "currentLevel": {
                "type": "string",
                "enum": ["novice", "beginner", "intermediate", "advanced", "expert"]
              },
              "desiredLevel": {
                "type": "string",
                "enum": ["novice", "beginner", "intermediate", "advanced", "expert"]
              },
              "priority": {
                "type": "string",
                "enum": ["low", "medium", "high", "critical"]
              },
              "confidenceLevel": {
                "type": "integer",
                "minimum": 1,
                "maximum": 10
              }
            }
          }
        }
      }
    },
    "competencyAssessments": {
      "type": "array",
      "description": "Domain-specific competency assessments",
      "items": {
        "type": "object",
        "required": ["competencyName", "domain", "currentLevel", "desiredLevel", "subcompetencies"],
        "properties": {
          "competencyName": {
            "type": "string",
            "description": "Name of the competency area"
          },
          "domain": {
            "type": "string",
            "enum": [
              "Software Development",
              "Cloud Architecture",
              "Data Science",
              "Cybersecurity",
              "DevOps",
              "Project Management",
              "Business Analysis",
              "UI/UX Design",
              "Quality Assurance",
              "System Administration",
              "Database Management",
              "Network Engineering"
            ]
          },
          "currentLevel": {
            "type": "string",
            "enum": ["novice", "beginner", "intermediate", "advanced", "expert"]
          },
          "desiredLevel": {
            "type": "string",
            "enum": ["novice", "beginner", "intermediate", "advanced", "expert"]
          },
          "subcompetencies": {
            "type": "array",
            "items": {
              "type": "object",
              "required": ["name", "currentLevel", "desiredLevel"],
              "properties": {
                "name": {
                  "type": "string"
                },
                "currentLevel": {
                  "type": "string",
                  "enum": ["novice", "beginner", "intermediate", "advanced", "expert"]
                },
                "desiredLevel": {
                  "type": "string",
                  "enum": ["novice", "beginner", "intermediate", "advanced", "expert"]
                },
                "priority": {
                  "type": "string",
                  "enum": ["low", "medium", "high", "critical"]
                }
              }
            }
          },
          "relevantExperiences": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "References to experiences from learner profile that demonstrate this competency"
          }
        }
      }
    },
    "learningPreferences": {
      "type": "object",
      "properties": {
        "preferredLearningStyle": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": ["Visual", "Auditory", "Kinesthetic", "Reading/Writing"]
          }
        },
        "timeCommitment": {
          "type": "object",
          "properties": {
            "hoursPerWeek": {
              "type": "integer",
              "minimum": 1,
              "maximum": 40
            },
            "preferredSchedule": {
              "type": "string",
              "enum": ["Mornings", "Afternoons", "Evenings", "Weekends", "Flexible"]
            }
          }
        },
        "challengePreference": {
          "type": "string",
          "enum": ["Conservative", "Moderate", "Aggressive"],
          "description": "Preferred learning challenge level"
        }
      }
    },
    "industryContext": {
      "type": "object",
      "properties": {
        "currentIndustry": {
          "type": "string"
        },
        "targetIndustries": {
          "type": "array",
          "items": {
            "type": "string"
          }
        },
        "companySizes": {
          "type": "array",
          "items": {
            "type": "string",
            "enum": ["Startup (1-50)", "Small (51-200)", "Medium (201-1000)", "Large (1001-5000)", "Enterprise (5000+)"]
          }
        }
      }
    },
    "assessmentMetadata": {
      "type": "object",
      "properties": {
        "assessmentDate": {
          "type": "string",
          "format": "date-time"
        },
        "completionTime": {
          "type": "integer",
          "description": "Time in minutes to complete assessment"
        },
        "assessmentVersion": {
          "type": "string",
          "default": "1.0"
        },
        "selfReflectionNotes": {
          "type": "string",
          "description": "Open-ended reflection on career goals and development priorities"
        }
      }
    },
    "version": {
      "type": "string",
      "description": "Schema version"
    }
  }
}{code}

{code:json}{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Cleansheet Learner Experience Profile Schema",
  "type": "object",
  "required": ["userName", "userGoals", "experiences", "exportDate", "version"],
  "properties": {
    "userName": {
      "type": "string",
      "description": "Learner's full name"
    },
    "userGoals": {
      "type": "string",
      "description": "Learner's career and professional development goals"
    },
    "experiences": {
      "type": "array",
      "description": "Professional experience entries",
      "items": {
        "type": "object",
        "required": ["organizationName", "role", "location", "startDate", "endDate", "technologies", "keySkills", "competencies", "projectTypes", "achievements", "description"],
        "properties": {
          "organizationName": {
            "type": "string",
            "description": "Name of the organization/company"
          },
          "role": {
            "type": "string",
            "description": "Job title or role"
          },
          "location": {
            "type": "string",
            "description": "Work location (city, state/country)"
          },
          "startDate": {
            "type": "string",
            "pattern": "^\\d{4}-\\d{2}$",
            "description": "Start date in YYYY-MM format"
          },
          "endDate": {
            "type": "string",
            "description": "End date in YYYY-MM format, empty string if current role"
          },
          "internalStakeholders": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Internal teams, departments, or individuals collaborated with"
          },
          "externalStakeholders": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "External partners, customers, or vendors worked with"
          },
          "technologies": {
            "type": "array",
            "items": {
              "type": "object",
              "required": ["name", "type"],
              "properties": {
                "name": {
                  "type": "string",
                  "description": "Technology, tool, or platform name"
                },
                "type": {
                  "type": "string",
                  "enum": ["Core", "Peripheral"],
                  "description": "Core = primary/essential, Peripheral = supporting/occasional"
                }
              }
            }
          },
          "keySkills": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Key skills demonstrated or developed in this role"
          },
          "competencies": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Professional competencies applied in this role"
          },
          "projectTypes": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Types of projects worked on (e.g., Agile, Research, Implementation)"
          },
          "achievements": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Notable accomplishments and quantifiable results"
          },
          "description": {
            "type": "string",
            "description": "Summary description of role responsibilities and impact"
          }
        }
      }
    },
